INSERT INTO `question` VALUES (2,'What value is printed for \"a\" below','d','1','2','3','no value',NULL,'In order to reference a global variable from a function the \"global\" keyword must be used inside the function','&lt;?php\r\n\r\n    $a = 1;\r\n\r\n    function Test()\r\n    {\r\n        echo \"a = $a\";\r\n    }\r\n    Test();\r\n?&gt;\r\n','global variables',1,2,42192,25827,3,'Alexander Saydakov',3);
