INSERT INTO `question` VALUES (4,'Which of the following is the way to create comments in PHP?','d','// commented code to end of line','/*    commented code here  */','# commented code to end of line','all of the above',NULL,'All three are valid.  They are borrowed from C, C++, and shell coding.',NULL,'comments',1,4,38330,22054,5,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (5,'A value that has no defined value is expressed in PHP with the following keyword:','b','undef','null','None','There is no such concept in PHP',NULL,'The case insensitive keyword NULL is used in PHP for no defined value.',NULL,'undefined values',1,5,32697,21174,124,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (6,'What will be printed by the code below?','b','SCALAR','empty value','STRING','\"not a reference\"',NULL,'ref(EXPR) returns FALSE if EXPR is not a reference, which is printed as empty value','my $val = \'x\';\r\n\r\nprint ref($val);','references',2,1,22520,12948,10,'Alexander Saydakov',3);
