INSERT INTO `question` VALUES (32,'What part of the string will be assigned to $match in the following code','a','am learning','I am learning Perl','learning','am','none','this will match everything from the first whitespace character to the last one','my $txt = \'I am learning Perl\';\r\n\r\nmy ($match) = $txt =~ /\\s(.*)\\s/;','regex',2,7,12452,6586,38,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (33,'A function call is always an rvalue.  True or false?','b','true','false',NULL,NULL,NULL,'A function call can be an lvalue if and only if the return value is a reference ',NULL,'lvalue rvalue',4,21,15546,9843,34,'Ivan Novick',3);
INSERT INTO `question` VALUES (34,'What value should be printed for x?','d','0','3','undefined','won\'t compile',NULL,'<code>int()</code> creates a temporary variable which is an rvalue.  The temporary variable that is created can not be assigned to since it is an rvalue. Thus this code should not compile','#include &lt;iostream&gt;\r\n\r\nint main()\r\n{\r\n    int x = int() = 3;\r\n\r\n    std::cout << x << std::endl;\r\n\r\n    return 0;\r\n}','temporaries',4,22,17060,10748,35,'Ivan Novick',3);
INSERT INTO `question` VALUES (35,'Which lines of code below should not compile?','e','14','14,15,18','14,17,18','14,15,17,18','14,15,16,18','Only reinterpret_cast can be used to convert a pointer to an object to a pointer to an unrelated object type.  The dynamic cast would fail at run-time, however on most compilers it will also fail to compile because there are no virtual functions in the class of the pointer being casted.','1  struct Foo\r\n2  {\r\n3  };\r\n4 \r\n5  struct Bar\r\n6  {\r\n7  };\r\n8 \r\n9 \r\n10 int main()\r\n11 {\r\n12     Foo* f = new Foo;\r\n13 \r\n14     Bar* b1 = f;\r\n15     Bar* b2 = static_cast&lt;Bar*&gt;(f);\r\n16     Bar* b3 = dynamic_cast&lt;Bar*&gt;(f);\r\n17     Bar* b4 = reinterpret_cast&lt;Bar*&gt;(f);\r\n18     Bar* b5 = const_cast&lt;Bar*&gt;(f);\r\n19 \r\n20     return 0;\r\n21 }\r\n','cast',4,23,19521,10345,36,'Ivan Novick',3);
INSERT INTO `question` VALUES (36,'In addition to const_cast, which cast can be used to cast away constness?','d','static_cast','dynamic_cast','reinterpret_cast','none of the above',NULL,'Only const_cast (or c-style casts) can be used to cast away constness ',NULL,'cast',4,24,17556,9549,37,'Ivan Novick',3);
