INSERT INTO `question` VALUES (38,'What will be the value of $1 after execution of the following code','c','none','I am learning Perl','I am learning','I','learning','this is a greedy match of any  characters to the last whitespace character','my $txt = \'I am learning Perl\';\r\n\r\n$txt =~ /(.*)\\s/;','regex',2,8,10038,6207,76,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (39,'In addition to c-style, which casts can be used to cast an int to a pointer or a pointer to an int?','c','static_cast','dynamic_cast','reinterpret_cast','static_cast or dynamic_cast','none of the above','Only reinterpret_cast (or c-style casts) can be used to cast an int to a pointer or a pointer to an int',NULL,'cast',4,26,19608,8333,40,'Ivan Novick',3);
INSERT INTO `question` VALUES (40,'Which lines of code below should cause the program to be undefined?','d','none','13','15','18','20','deleting NULL pointers have no effect.  deleting a pointer to a base class which points to a derived object is legal assuming the base destructor is virtual. deleting an array of objects using a base class pointer is undefined.',' 1 struct Foo\r\n 2 {\r\n 3   virtual ~Foo() {}\r\n 4 };\r\n 5\r\n 6 struct Bar : public Foo\r\n 7 {\r\n 8 };\r\n 9\r\n10 int main(int argc, char** argv)\r\n11 {\r\n12   Foo* f = new Bar;\r\n13   delete f;\r\n14   f = 0;\r\n15   delete f;\r\n16\r\n17   Foo* fa = new Bar[10];\r\n18   delete [] fa;\r\n19   fa = 0;\r\n20   delete fa;\r\n21\r\n22   return 0;\r\n23 }','delete',4,27,21300,9186,41,'Ivan Novick',3);
INSERT INTO `question` VALUES (41,'What is the value of x at the end of main?','c','1','-1','implementation defined','undefined','compiler error','if either operand to a modulus operator is negative the result is implementation defined','int main(int argc, char** argv)\r\n{\r\n  int x = 50 % -7;\r\n  return 0;\r\n}','modulus',4,28,18891,8408,42,'Ivan Novick',3);
INSERT INTO `question` VALUES (42,'If assumptions in the code below are true what value would be the final value printed by this program?','e','0x60000000','0x60000003','0x6000000C','implementation defined','undefined','In pointer addition and subtraction if both operands and the result do not point inside the same array (or 1 past the last element of the array) the result is undefined.','int main(int argc, char** argv)\r\n{\r\n  // assume result printed is 4\r\n  std::cout << sizeof(int) << std::endl;\r\n\r\n  int *x = new int;\r\n  \r\n  // assume result printed is 0x60000000\r\n  std::cout << x << std::endl;\r\n\r\n  std::cout << x + 3 << std::endl;\r\n\r\n  return 0;\r\n}','pointer arithmetic',4,29,17259,6802,43,'Ivan Novick',3);
INSERT INTO `question` VALUES (43,'Given the assumptions in the comments, what values is printed for x?','d','0x00000000','0xFFFFFFFF','implementation defined','undefined',NULL,'The result of a shift operation is undefined if the right operand is negative, or greater than or equal to the length in bits of the promoted left operand','int main(int argc, char** argv)\r\n{\r\n  // assume result printed is 4\r\n  std::cout << sizeof(int) << std::endl;\r\n\r\n  int x = 0x1000;\r\n\r\n  x = x << 32;\r\n  \r\n  std::cout << std::hex << x << std::endl;\r\n\r\n  return 0;\r\n}','shift operators',4,30,14755,6598,44,'Ivan Novick',3);
INSERT INTO `question` VALUES (44,'Will &quot;Hello World&quot; be printed by the program below?','a','yes','no','implementation defined','unspecified','undefined','Pointer comparison with greater and less than relational operators is defined within the same array.','int main(int argc, char** argv)\r\n{\r\n  int array[33];\r\n\r\n  if ( &array[4] < &array[23] )\r\n  {\r\n    std::cout << \"Hello World\" << std::endl;\r\n  }\r\n\r\n  return 0;\r\n}','relational operators with pointers',4,31,15715,9480,45,'Ivan Novick',3);
INSERT INTO `question` VALUES (45,'Will &quot;Hello World&quot; be printed by the program below?','a','yes','no','implementation defined','unspecified','undefined','Relational operators can be used to do pointer comparison of non-static members in the same object where the definitions of the members is not seperated by an access specifier.  The member declared first will evaluate to lower.  Note: other than this case, the only other time relational operators can be used to compare pointers is when the addresses point to elements in the same array.  For all other cases, the result of relational operators on pointers is unspecified.','struct Foo\r\n{\r\n  int x;\r\n  int y;\r\n};\r\n\r\nint main(int argc, char** argv)\r\n{\r\n  Foo f;\r\n\r\n  if ( &f.x < &f.y )\r\n  {\r\n    std::cout << \"Hello World\" << std::endl;\r\n  }\r\n\r\n  return 0;\r\n}','relational operators with pointers',4,32,16744,8329,46,'Ivan Novick',3);
INSERT INTO `question` VALUES (46,'True or False, the program below will print NOT EQUAL','b','true','false',NULL,NULL,NULL,'Distinct pointer types can not be compared with equality operators','#include &lt;iostream&gt;\r\n\r\nstruct Foo\r\n{\r\n};\r\n\r\nstruct Bar \r\n{\r\n};\r\n\r\nint main(int argc, char** argv)\r\n{\r\n  Foo* f = new Foo;\r\n  Bar* b = new Bar;\r\n\r\n  if ( f == b )\r\n    std::cout << \"EQUAL\" << std::endl;\r\n  else\r\n    std::cout << \"NOT EQUAL\" << std::endl;\r\n\r\n  return 0;\r\n}','equality operators with pointers',4,33,12066,5805,47,'Ivan Novick',3);
INSERT INTO `question` VALUES (47,'What value gets printed by the program?','a','1','2','3','4','undefined','If the first operand to the logical AND operator evaluates to false the second operand is guaranteed not to evaluate.  Note: the logical OR operator is guaranteed not to evaluate the second operand if the first operand is true. Note: for the logical AND and OR operators, all side effects of the first expression, except for destruction of temporaries, happen before the second expression is evaluated.','#include &lt;iostream&gt;\r\n\r\nint main(int argc, char** argv)\r\n{\r\n  int x = 0;\r\n  int y = 0;\r\n\r\n  if (x++ && y++)\r\n  {\r\n    y += 2;\r\n  }\r\n\r\n  std::cout << x + y << std::endl;\r\n\r\n  return 0;\r\n}','logical operators',4,34,20848,8723,48,'Ivan Novick',3);
INSERT INTO `question` VALUES (48,'What value gets printed by the program?','d','0','3','11','21','undefined','The 2nd and 3rd operands to the conditional operator must be of the same type.  If one of the two can be converted to the other, the conversion will occur.  Note: if a conversion exists for each of the operands into the other\'s type, the program is ill-formed.','#include &lt;iostream&gt;\r\n\r\nstruct Foo\r\n{\r\n  int x;\r\n\r\n  operator int()\r\n  {\r\n    return 21;\r\n  }\r\n};\r\n\r\nint main(int argc, char** argv)\r\n{\r\n  Foo f;\r\n  f.x = 11;\r\n\r\n  std::cout << (0?3:f) << std::endl;\r\n\r\n  return 0;\r\n}','conditional operator',4,35,14988,7923,49,'Ivan Novick',3);
INSERT INTO `question` VALUES (49,'What value gets printed by the program?','a','1','2','3','6','undefined','The operands to the comma operator are evaluated from left to right.  The value of the left hand expression is discarded.  The type and value of the result are the type type and value of the right hand operand.  Note: assignment takes precedence over the comma operator, so in this case x=1 is evaluated first; than x, 2, 3','#include &lt;iostream&gt;\r\n\r\nint main(int argc, char** argv)\r\n{\r\n  int x;\r\n\r\n  x = 1, 2, 3;\r\n\r\n  std::cout << x << std::endl;\r\n\r\n  return 0;\r\n}','comma operator',4,36,15083,8216,50,'Ivan Novick',3);
INSERT INTO `question` VALUES (50,'What value gets printed by the program?','e','0','1','2','3','nothing, it is ill-formed','Jumping into a block and bypassing a declaration with an initialization is ill-formed.  In this example there are cases which would bypass the declaration and initialization of block scope x declaration.','#include &lt;iostream&gt;\r\n\r\nint main() \r\n{ \r\n  int x = 3;\r\n\r\n  switch(x)\r\n  {\r\n    case 0:\r\n      int x = 1;\r\n      std::cout << x << std::endl;\r\n    break;\r\n    case 3:\r\n      std::cout << x << std::endl;\r\n    break;\r\n    default:\r\n      x = 2;\r\n      std::cout << x << std::endl;\r\n  }\r\n\r\n  return 0;\r\n} \r\n','switch',4,37,13924,6895,51,'Ivan Novick',3);
INSERT INTO `question` VALUES (51,'What value gets printed by the program?','d','0','3','3.14','nothing, its ill-formed','implementation-defined','Any construct that could possibly be considered a declaration is a declaration.  In this example, the second line of main is interpreted as a function declaration in preference to an object declaration with initialization.','#include &lt;iostream&gt;\r\n\r\nstruct Foo\r\n{\r\n  Foo(int d) : x(d) {}\r\n  int x;\r\n};\r\n\r\nint main() \r\n{ \r\n  double x = 3.14;\r\n\r\n  Foo f( int(x) );\r\n\r\n  std::cout << f.x << std::endl;\r\n\r\n  return 0;\r\n} ','declarations',4,38,14668,6676,52,'Ivan Novick',3);
INSERT INTO `question` VALUES (52,'The code below is legal.  True or false?','a','true','false',NULL,NULL,NULL,'String literals have static storage duration, therefore they can be referenced anywhere in the translation unit, even though it is defined in a function','#include &lt;iostream&gt;\r\n\r\nconst char* Foo()\r\n{\r\n  return \"Hello World\";\r\n}\r\n\r\nint main() \r\n{ \r\n  const char* str = Foo();\r\n  std::cout << str << std::endl;\r\n\r\n  return 0;\r\n}','string literals',4,39,9286,7136,53,'Ivan Novick',3);
INSERT INTO `question` VALUES (53,'The code below would declare an array of references, if we had properly initalized the references.  True or false?','b','true','false',NULL,NULL,NULL,'There is no such thing as an array of references.  Also there are no references to references or pointers to references.','int main() \r\n{ \r\n  int& x[50];\r\n\r\n  return 0;\r\n} ','references',4,40,9595,6967,54,'Ivan Novick',3);
INSERT INTO `question` VALUES (54,'Which of the following operators does NOT indicate a sequence point in the code?','d','&&','||','?','=',',','All of them except the assignment operator indicate a sequence point.',NULL,'sequence points',4,41,12250,6556,55,'Ivan Novick',3);
INSERT INTO `question` VALUES (55,'What value gets printed by the program?','d','9','15','21','ill-formed','undefined','Only the first constant expression in a multiple dimension array can be ommitted.  Therefore the declaration of the array in this example is ill-formed.','#include &lt;iostream&gt;\r\n\r\nint main() \r\n{ \r\n  int sum = 0;\r\n\r\n  int array[3][] = {{0, 1, 2}, {3, 4, 5}, {6, 7, 8}};\r\n\r\n  for (int i = 0; i < 3 ; ++i)\r\n  {\r\n    for (int j = 2; j < 3 ; j++)\r\n    {\r\n      sum += array[i][j];\r\n    }\r\n  }\r\n\r\n  std::cout << sum << std::endl;\r\n\r\n  return 0;\r\n} ','arrays',4,42,11433,5564,56,'Ivan Novick',3);
INSERT INTO `question` VALUES (56,'What value gets printed by the program?','d','3','5','6','ill-formed','undefined','There can only be one function with the same signature. Altering the cv qualification of parameters does not change the function signature.  Therefore the two foo functions have the same signature and the program is ill-formed.','#include &lt;iostream&gt;\r\n\r\nint foo(int x, int y)\r\n{\r\n  return x+y;\r\n}\r\n\r\nint foo(const int x, const int y)\r\n{\r\n  return x+y+1;\r\n}\r\n\r\nint main(int argc, char** argv)\r\n{\r\n  const int x = 3;\r\n  const int y = 2;\r\n\r\n  std::cout << foo(x,y) << std::endl;\r\n\r\n  return 0;\r\n}','functions',4,43,11065,6237,57,'Ivan Novick',3);
INSERT INTO `question` VALUES (57,'What value gets printed by the program?','d','3','8','11','ill-formed','undefined','The return type of a function does not alter the signature (for function overloading).  However, the return type of a function DOES change the &quot;type&quot of the function and thus the assignment of foo to ptr in not valid.','#include &lt;iostream&gt;\r\n\r\nint foo(int x, int y)\r\n{\r\n  return x+y;\r\n}\r\n\r\ndouble foo(double x, double y)\r\n{\r\n  return x+y;\r\n}\r\n\r\nint main(int argc, char** argv)\r\n{\r\n  double (*ptr)(int, int);\r\n\r\n  ptr = foo;\r\n\r\n  std::cout << ptr(3,8) << std::endl;\r\n\r\n  return 0;\r\n}','functions',4,44,10249,6525,58,'Ivan Novick',3);
INSERT INTO `question` VALUES (58,'What value gets printed by the program?','b','3','4','9','ill-formed','undefined','The trick here is, we change the parameter names from the function declaration to function definition.  This is legal, and only the parameter names in the function definition are used.','#include &lt;iostream&gt;\r\n\r\nint foo(int y);\r\nint foo(int x)\r\n{\r\n  return x+1;\r\n}\r\n\r\nint main(int argc, char** argv)\r\n{\r\n  int x = 3;\r\n  int y = 6;\r\n\r\n  std::cout << foo(x) << std::endl;\r\n\r\n  return 0;\r\n}','functions',4,45,10345,7251,59,'Ivan Novick',3);
INSERT INTO `question` VALUES (59,'What value gets printed by the program?','d','1','3','5','ill-formed','undefined','The program is ill-formed.  Parameters of a function can not be used in default argument expressions.','#include &lt;iostream&gt;\r\n\r\nint foo(int x, int y = x) \r\n{ \r\n  return x+y+1; \r\n} \r\n\r\nint main(int argc, char** argv) \r\n{ \r\n  std::cout << foo(2) << std::endl; \r\n  return 0; \r\n}','default arguments',4,46,9314,6049,60,'Ivan Novick',3);
INSERT INTO `question` VALUES (60,'Which, if any, of the member function definitions below are ill-formed?','b','a','b','c','a and b','none','b is ill-formed.  Non-static members can not be used as default arguments.','#include &lt;iostream&gt;\r\n\r\nint g_x = 44; \r\n\r\nstruct Foo \r\n{ \r\n  int m_x; \r\n\r\n  static int s_x; \r\n\r\n  Foo(int x) : m_x(x) {} \r\n\r\n  int a(int x = g_x) \r\n  { \r\n    return x + 1; \r\n  } \r\n\r\n  int b(int x = m_x) \r\n  { \r\n    return x + 1; \r\n  } \r\n\r\n  int c(int x = s_x) \r\n  { \r\n    return x + 1; \r\n  } \r\n}; \r\n\r\nint Foo::s_x = 22; \r\n\r\nint main(int argc, char** argv) \r\n{ \r\n  Foo f(6); \r\n\r\n  std::cout << f.a() << std::endl; \r\n  std::cout << f.b() << std::endl; \r\n  std::cout << f.c() << std::endl; \r\n\r\n  return 0; \r\n}','default arguments',4,47,8741,4414,61,'Ivan Novick',3);
INSERT INTO `question` VALUES (61,'What is the output of the program?','b','10','15','20','30','ill-formed','The method B::foo is called but with the default argument of 5 from A::foo.  &quot;A virtual function call uses the default arguments in the declaration of the virtual function determined by the static type of the pointer or reference denoting the object. An overriding function in a derived class does not acquire default arguments from the function it overrides.&quot;','#include &lt;iostream&gt;\r\n\r\nstruct A \r\n{ \r\n  virtual int foo(int x = 5) \r\n  { \r\n    return x * 2; \r\n  } \r\n}; \r\n\r\nstruct B : public A \r\n{ \r\n  int foo(int x = 10) \r\n  { \r\n    return x * 3; \r\n  } \r\n}; \r\n\r\nint main(int argc, char** argv) \r\n{ \r\n  A* a = new B; \r\n\r\n  std::cout << a->foo() << std::endl; \r\n\r\n  return 0; \r\n}','default arguments',4,48,13436,5641,62,'Ivan Novick',3);
INSERT INTO `question` VALUES (62,'What is the output of the program?','b','72','75.14','ill-formed','undefined',NULL,'&quot;An aggregate is an array or a class with no user-declared constructors, no private or protected non-static data members, no base classes, and no virtual functions.&quot;  Aggregates can be initialized by &quot;brace-enclosed, comma separated list of initializer-clauses for the members of the aggregate, written in increasing subscript or member order.&quot;  Static data members are skipped during this type of initialization so 3.14 initializes d and not sd in this example  &quot;If there are fewer initializers in the list than there are members in the aggregate, then each member not explicitly initialized shall be value-initialized&quot;. So in this example i is value-initialized to 0.','#include &lt;iostream&gt;\r\n\r\nclass Foo\r\n{\r\npublic:\r\n\r\n  char c;\r\n  static double sd;\r\n  double d;\r\n  int i;\r\n};\r\n\r\nint main(int argc, char** argv) \r\n{ \r\n  Foo f = { 72, 3.14 };\r\n\r\n  std::cout << f.c + f.d + f.i << std::endl;\r\n\r\n  return 0; \r\n}','initialization',4,49,12087,5670,63,'Ivan Novick',3);
INSERT INTO `question` VALUES (63,'What is the output of the program?','c','3','0x822222222','ill-formed','undefined',NULL,'References can not be initialized with values, so this program is ill-formed.  In this case rpx should be initialized with a pointer to an int, not with the address of an int.','#include &lt;iostream&gt;\r\n\r\nint main(int argc, char** argv) \r\n{ \r\n  // assume address of x is 0x822222222\r\n  int x = 3;\r\n\r\n  int*& rpx = &x;\r\n\r\n  std::cout << rpx << std::endl;\r\n  \r\n  return 0; \r\n}','references',4,50,8460,5502,64,'Ivan Novick',3);
INSERT INTO `question` VALUES (64,'What is the output of the program?','c','Foo','Bar','ill-formed','undefined','unspecified','The code is ill-formed.  Non-const member functions can not be called on const objects.','#include &lt;iostream&gt;\r\n\r\nstruct Foo\r\n{\r\n  Foo() {} \r\n\r\n  void go()\r\n  {\r\n    std::cout << \"Foo\" << std::endl;\r\n  }\r\n};\r\n\r\nstruct Bar : public Foo\r\n{\r\n  Bar() {}\r\n\r\n  void go()\r\n  {\r\n    std::cout << \"Bar\" << std::endl;\r\n  }\r\n};\r\n\r\nint main(int argc, char** argv) \r\n{ \r\n  Bar b;\r\n\r\n  const Foo f = b;\r\n\r\n  f.go();\r\n\r\n  return 0; \r\n}','member functions',4,51,8101,4246,65,'Ivan Novick',3);
INSERT INTO `question` VALUES (65,'Static member functions of classes can be virtual. True or false?','b','true','false',NULL,NULL,NULL,'Static member functions can not be virtual',NULL,'static member functions',4,52,7505,6192,66,'Ivan Novick',3);
INSERT INTO `question` VALUES (66,'What is the output of the program?','c','0','3','ill-formed','undefined','unspecified','The code is ill-formed.  The non-static members of a base class, that is an indirect or direct base class more than once, can not be referred to without qualification.','#include &lt;iostream&gt;\r\n\r\nstruct BS\r\n{\r\n  unsigned int color;\r\n};\r\n\r\nstruct car : public BS\r\n{\r\n};\r\n\r\nstruct truck : public BS\r\n{\r\n};\r\n\r\nstruct city : public car, public truck\r\n{\r\n};\r\n\r\nint main(int argc, char** argv) \r\n{ \r\n  city c;\r\n\r\n  c.color = 3;\r\n\r\n  std::cout << c.color << std::endl;\r\n\r\n  return 0; \r\n}','inheritance',4,53,9073,5875,67,'Ivan Novick',3);
INSERT INTO `question` VALUES (67,'How many times is Hello World printed by this program?','c','1','2','3','code is ill-formed','undefined','3 times.  One time for the first virtual occurences of BS in the heirarchy and once for each non-virtual occurence of BS.  mid1 and mid2 together have one.  mid3 and mid4 each have one.','#include &lt;iostream&gt;\r\n\r\nstruct BS\r\n{\r\n  BS()\r\n  {\r\n    std::cout << \"Hello World\" << std::endl;\r\n  }\r\n  unsigned int color;\r\n};\r\n\r\nstruct mid1 : virtual public BS { };\r\nstruct mid2 : virtual public BS { };\r\nstruct mid3 : public BS { };\r\nstruct mid4 : public BS { };\r\n\r\nstruct DR : public mid1, public mid2, \r\n            public mid3, public mid4 { };\r\n\r\nint main(int argc, char** argv) \r\n{ \r\n  DR d;\r\n  return 0; \r\n}','inheritance',4,54,8360,4686,68,'Ivan Novick',3);
INSERT INTO `question` VALUES (68,'What gets printed by this program?','a','SHAPE','BOX','undefined','code is ill-formed','unspecified','Shape::print is not a virtual function.  Therefore Shape::print is called even though s1 points to a Box object.  If print was virtual, then BOX would be printed.','#include &lt;iostream&gt;\r\n\r\nstruct Shape\r\n{\r\n  void print()\r\n  {\r\n    std::cout << \"SHAPE\" << std::endl;\r\n  }\r\n};\r\n\r\nstruct Box : public Shape\r\n{\r\n  void print()\r\n  {\r\n    std::cout << \"BOX\" << std::endl;\r\n  }\r\n};\r\n\r\nint main(int argc, char** argv)\r\n{\r\n  Shape* s1 = new Box;\r\n\r\n  s1->print();\r\n\r\n  return 0;\r\n}','virtual functions',4,55,9109,6472,69,'Ivan Novick',3);
INSERT INTO `question` VALUES (69,'What gets printed by this program?','e','SHAPE','BOX','SPHERE','undefined','code is ill-formed','The code is ill-formed.  Virtual functions must have a unique \"final overrider\" that overrides all other instances of that function in its inheritance heirarchy.  In this case neither Box::print nor Sphere::print override each other, so the condition is not met and the GeoDisc class is ill-formed.','#include &lt;iostream&gt;\r\n\r\nstruct Shape\r\n{\r\n  virtual void print()\r\n  {\r\n    std::cout << \"SHAPE\" << std::endl;\r\n  }\r\n  virtual ~Shape() {}\r\n};\r\n\r\nstruct Box : public virtual Shape\r\n{\r\n  void print()\r\n  {\r\n    std::cout << \"BOX\" << std::endl;\r\n  }\r\n};\r\n\r\nstruct Sphere : public virtual Shape\r\n{\r\n  void print()\r\n  {\r\n    std::cout << \"SPHERE\" << std::endl;\r\n  }\r\n};\r\n\r\nstruct GeoDisc : public Box, public Sphere\r\n{\r\n};\r\n\r\nint main(int argc, char** argv)\r\n{\r\n  Shape* s = new GeoDisc;\r\n\r\n  s->print();\r\n\r\n  delete s;\r\n\r\n  return 0;\r\n}','virtual functions',4,56,8137,4842,70,'Ivan Novick',3);
INSERT INTO `question` VALUES (70,'How many times is Hello World printed by this program?','c','0','3','4','undefined','code is ill-formed','4 times. Virtual inheritance affects the class that is inherited virtually. Therefore the BS base class is not inherited virtually and there are 4 of them within 1 DR object. The virtual inheritance statements in this code are affecting the classes mid1, mid2, mid3, mid4 and not BS.','#include &lt;iostream&gt;\r\n\r\nstruct BS\r\n{\r\n  BS()\r\n  {\r\n    std::cout << \"Hello World\" << std::endl;\r\n  }\r\n};\r\n\r\nstruct mid1 : public BS { };\r\nstruct mid2 : public BS { };\r\nstruct mid3 : public BS { };\r\nstruct mid4 : public BS { };\r\n\r\nstruct DR : public virtual mid1, public virtual mid2, public virtual mid3, public mid4 { };\r\n\r\nint main(int argc, char** argv) \r\n{ \r\n  DR d;\r\n  return 0; \r\n}','inheritance',4,57,7628,5077,71,'Ivan Novick',3);
INSERT INTO `question` VALUES (71,'What gets printed by this program?','a','SHAPE','BOX','undefined','code is ill-formed','unspecified','SHAPE is printed.  A function with a different signature does not override a function in a base class even if the function name is the same.','#include &lt;iostream&gt;\r\n\r\nstruct Shape\r\n{\r\n  virtual void print()\r\n  {\r\n    std::cout << \"SHAPE\" << std::endl;\r\n  }\r\n\r\n  virtual ~Shape() {}\r\n};\r\n\r\nstruct Box : public Shape\r\n{\r\n  virtual void print(int i)\r\n  {\r\n    std::cout << \"BOX\" << std::endl;\r\n  }\r\n};\r\n\r\nint main(int argc, char** argv) \r\n{ \r\n  Shape* s = new Box;\r\n\r\n  s->print();\r\n\r\n  delete s;\r\n\r\n  return 0; \r\n}','virtual functions',4,58,8084,4798,72,'Ivan Novick',3);
INSERT INTO `question` VALUES (72,'What gets printed by this program?','b','SHAPE','BOX','undefined','code is ill-formed','unspecified','BOX is printed.  The return type of an overriding virtual function must have either the same type as the function it is overriding or both functions must return a pointer or reference with the same cv-qualifications whereby the class pointed or reffered to in the overridden function is an unambiguous and accessible direct or indirect base class of the class pointed or referred to in the overriding function.','#include &lt;iostream&gt;\r\n\r\nstruct Shape\r\n{\r\n  virtual Shape* duplicate()\r\n  {\r\n    std::cout << \"SHAPE\" << std::endl;\r\n    return new Shape;\r\n  }\r\n  virtual ~Shape() {}\r\n};\r\n\r\nstruct Box : public Shape\r\n{\r\n  virtual Box* duplicate()\r\n  {\r\n    std::cout << \"BOX\" << std::endl;\r\n    return new Box;\r\n  }\r\n};\r\n\r\nint main(int argc, char** argv) \r\n{ \r\n  Shape* s1 = new Box;\r\n\r\n  Shape* s2 = s1->duplicate();\r\n\r\n  delete s1;\r\n  delete s2;\r\n  return 0; \r\n}','virtual functions',4,59,7924,5262,73,'Ivan Novick',3);
INSERT INTO `question` VALUES (73,'What gets printed by this program?','d','SHAPE','BOX','undefined','code is ill-formed','unspecified','The program is ill-formed.  The return type of the virtual function that is called dynamically is converted to the return type of the overridden function.  In this case the result of the duplicate() function is a pointer to a Box object but converted to a Shape pointer.  Therefore the assignment from the return value to b1 is ill-formed.','#include &lt;iostream&gt;\r\n\r\nstruct Shape\r\n{\r\n  virtual Shape* duplicate()\r\n  {\r\n    return new Shape;\r\n  }\r\n  virtual void print()\r\n  {\r\n    std::cout << \"SHAPE\" << std::endl;\r\n  }\r\n  virtual ~Shape() {}\r\n};\r\n\r\nstruct Box : public Shape\r\n{\r\n  virtual Box* duplicate()\r\n  {\r\n    return new Box;\r\n  }\r\n  virtual void print()\r\n  {\r\n    std::cout << \"BOX\" << std::endl;\r\n  }\r\n};\r\n\r\nint main(int argc, char** argv) \r\n{ \r\n  Shape* s1 = new Box;\r\n\r\n  Box* b1 = s1->duplicate();\r\n\r\n  b1->print();\r\n\r\n  delete s1;\r\n  delete b1;\r\n  return 0; \r\n}','virtual functions',4,60,8170,3299,74,'Ivan Novick',3);
INSERT INTO `question` VALUES (74,'What gets printed by this program?','d','SHAPE','BOX','undefined','code is ill-formed','unspecified','An implicit conversion from a pointer to a derived class, to a pointer to an inaccessible (private inheritance) base class is ill-formed.  Thus the assignment of the new Box to Shape* is ill-formed.','#include &lt;iostream&gt;\r\n\r\nstruct Shape\r\n{\r\n  virtual void print()\r\n  {\r\n    std::cout << \"SHAPE\" << std::endl;\r\n  }\r\n  virtual ~Shape() {}\r\n};\r\n\r\nstruct Box : private Shape\r\n{\r\n  virtual void print()\r\n  {\r\n    std::cout << \"BOX\" << std::endl;\r\n  }\r\n};\r\n\r\nint main(int argc, char** argv) \r\n{ \r\n  Shape* s = new Box;\r\n\r\n  s->print();\r\n\r\n  delete s;\r\n\r\n  return 0; \r\n}','access control',4,61,7533,3630,75,'Ivan Novick',3);
INSERT INTO `question` VALUES (75,'Which lines below should not compile?','b','none','17','18','19','20','Line 17 should not compile.  Access to non-static protected members of a base class can not be via a pointer or reference to the base class.','1 #include &lt;iostream&gt;\r\n2\r\n3 class Bar\r\n4 {\r\n5 protected:\r\n6   static int x;\r\n7   int y;\r\n8 };\r\n9\r\n10 int  Bar::x = 33;\r\n11\r\n12 class Barrel : public Bar\r\n13 {\r\n14 public:\r\n15   void foo(Bar* b, Barrel* d)\r\n16   {\r\n17     b->y = 0;\r\n18     d->y = 0;\r\n19     Bar::x = 0;\r\n20     Barrel::x = 0;\r\n21   }\r\n22 };\r\n23\r\n24 int main(int argc, char** argv)\r\n25 {\r\n26   Barrel b;\r\n27   b.foo(&b, &b);\r\n28   return 0;\r\n29 }','protected access control',4,62,7290,2756,82,'Ivan Novick',3);
INSERT INTO `question` VALUES (76,'What will be the size of the @fields array','d','0','1','3','5',NULL,'split() with -1 as the limit will preserve empty fields at the end. Leading empty fields are always preserved except a special case with omitted pattern or pattern of \' \' (space)','my $record = \':a:b:c:\'; \r\n\r\nmy @fields = split(\':\', $record, -1);','split',2,9,12721,6090,77,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (77,'What will be the output of the code below?','a','blah','Can\'t locate object method \"new\" via package \"B\"',NULL,NULL,NULL,'new() is a regular method and is also inherited','package A;\r\n\r\nsub new {\r\n    my $class = shift;\r\n    return bless {}, $class;\r\n}\r\n\r\n \r\npackage B;\r\nuse base \'A\';\r\n\r\nsub foo {\r\n    return \'blah\';\r\n}\r\n\r\n\r\npackage main;\r\n\r\nmy $obj = B->new();\r\n\r\nprint $obj->foo(), \"\\n\";','object-oriented Perl',2,10,6017,4498,78,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (78,'The following operator will collapse all duplicate spaces into single spaces in the string $str','b','true','false',NULL,NULL,NULL,'it will collapse only the first occurrence, the \'g\' modifier is needed to replace globally: s/\\s+/ /g','$str =~ s/\\s+/ /;','substitution operator',2,11,6968,3907,79,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (79,'What elements will the @a array consist of?','d','\' \', \'a\', \' \', \'b\', \' \', \'c\', \' \'','\'\', \'a\', \'b\', \'c\', \'\'','undef, \'a\', \'b\', \'c\', undef','\'a\', \'b\', \'c\'','\' \', \'a\', \'b\', \'c\'','This is a special case emulating awkâ€™s default behavior: splits on whitespace after skipping any leading whitespace. Trailing empty fields are not produced unless limit is specified','$_ = \' a b c \';\r\n\r\nmy @a = split();','split',2,12,9901,5226,80,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (80,'What gets printed by the following program?','a','list context','scalar context','nothing',NULL,NULL,'foo() is assigned to a list (of one element), so wantarray returns true','my ($x) = foo();\r\nprint \"$x\\n\";\r\n\r\nsub foo {\r\n  return wantarray ? \'list context\' : \'scalar context\';\r\n}\r\n','context',2,13,7026,4451,81,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (81,'All three ways of calling new() are equivalent in the code below','a','true','false',NULL,NULL,NULL,'The first two calls implicitly pass the package name of \'A\' as the first parameter. The last call has the form of a static one, but passes \'A\' explicitly, which is not a usual practice, but possible','package A;\r\n\r\nsub new {\r\n    my ($class, $param) = @_;\r\n    return bless {param => $param}, $class;\r\n}\r\n\r\n\r\npackage main;\r\n\r\nmy $a1 = A->new(\'foo\');\r\nmy $a2 = new A(\'foo\');\r\nmy $a3 = A::new(\'A\', \'foo\');\r\n','object-oriented Perl',2,14,4457,3409,92,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (82,'Which lines below should not compile?','b','none','9','10','11','9 and 10 and 11','If any user-declared constructor is present in the class, then no default constructor will be created implicitly.  Additionally if no user declared copy constructor is declared, then an implicit copy constructor will be created by the compiler.  In this example there is a user-declared constructor which prevents the default constructor from existing, but a copy constructor is still created implicitly.','1 struct A\r\n2 {\r\n3    A(int x) : n(x) {}\r\n4    int n;\r\n5 };\r\n6\r\n7 int main(int argc, char** argv)\r\n8 {\r\n9    A a1;\r\n10   A a2(2);\r\n11   A a3(a2);\r\n12   return 0;\r\n13 }','copy constructors',4,63,10012,5546,83,'Ivan Novick',3);
INSERT INTO `question` VALUES (83,'Which of the following statements are true?','d','1','2','3','1 and 3','2 and 3','The 2nd statement is not true','1) Conversion functions of class A convert from\r\n   class A into another type\r\n\r\n2) Conversion functions of class A are used to\r\n   convert from another given type into class A\r\n\r\n3) Converting constructors must be callable with\r\n   a single argument','user defined conversions',4,64,8155,4172,84,'Ivan Novick',3);
INSERT INTO `question` VALUES (84,'What gets printed for the value of z?','c','3','4','5','code is ill-formed','undefined','There is a sequence point after the initalization of each base and member, thus the code is well-formed and defined<br><br>','#include &lt;iostream&gt;\r\n\r\nstruct Foo\r\n{\r\n  Foo(int n) : x(n++), y(n++), z(n++) {}\r\n  int x;\r\n  int y;\r\n  int z;\r\n};\r\n\r\nint main(int argc, char** argv)\r\n{\r\n  Foo f(3);\r\n\r\n  std::cout << \"x: \" << f.x << std::endl;\r\n  std::cout << \"y: \" << f.y << std::endl;\r\n  std::cout << \"z: \" << f.z << std::endl;\r\n\r\n  return 0;\r\n}','member initialization',4,65,8504,5196,85,'Ivan Novick',3);
INSERT INTO `question` VALUES (85,'What gets printed?','a','20000','22000','30000','33000','code is ill-formed','20000 is output.<br>All sub-objects representing virtual base classes are initialized by the constructor of the most derived class.  If the constructor of the most derived class does not specify a mem-initializer for a virtual base class V, then V\'s default construtor is called to initialize the virtual base class subobject.','#include &lt;iostream&gt;\r\n\r\nstruct Car\r\n{\r\n  Car() : price(20000) {}\r\n  Car(double b) : price(b*1.1) {}\r\n  double price;\r\n};\r\n\r\nstruct Toyota : public virtual Car\r\n{\r\n  Toyota(double b) : Car(b) {}\r\n};\r\n\r\nstruct Prius : public Toyota\r\n{\r\n  Prius(double b) : Toyota(b)  {}\r\n};\r\n\r\nint main(int argc, char** argv)\r\n{\r\n  Prius p(30000);\r\n\r\n  std::cout << p.price << std::endl;\r\n\r\n  return 0;\r\n}','member initialization',4,66,6602,2414,86,'Ivan Novick',3);
INSERT INTO `question` VALUES (86,'What is the maximum number of implicitly defined constructors that this struct will have?','a','0','1','2','implementation specified','undefined','There will be no implicity defined constructors for this struct.<br>','struct A\r\n{\r\n  A(A& a) { }\r\n  A(double d) {}\r\n  int val;\r\n};','Copying class objects',4,67,7443,4127,87,'Ivan Novick',3);
INSERT INTO `question` VALUES (87,'What gets printed?','d','0','5','10','code is ill-formed','undefined','The program is ill-formed.  The third line of main tries to initialize a3 with a2, but A\'s copy constructor takes a non-const reference which violates a2\'s const declaration.','#include &lt;iostream&gt;\r\n\r\nstruct A\r\n{\r\n  A() : val(0) {}\r\n  A(int v) : val(v) {}\r\n  A(A& a) : val(a.val) {} \r\n\r\n  int val;\r\n};\r\n\r\nint main(int argc, char** argv)\r\n{\r\n  const A a1;\r\n  const A a2(5);\r\n  const A a3 = a2;\r\n\r\n  std::cout << a1.val + a2.val + a3.val << std::endl;\r\n\r\n  return 0;\r\n}','Copying class objects',4,68,6208,2861,88,'Ivan Novick',3);
INSERT INTO `question` VALUES (88,'What gets printed?','d','0','5','10','code is ill-formed','unspecified','It is illegal to have a constructor whose first and only non-default argument is a value parameter for the class type.','#include &lt;iostream&gt;\r\n\r\nstruct A\r\n{\r\n  A() : val() {}\r\n  A(int v) : val(v) {}\r\n  A(A a) : val(a.val) {} \r\n\r\n  int val;\r\n};\r\n\r\nint main(int argc, char** argv)\r\n{\r\n  A a1(5);\r\n  A a2(a1);\r\n\r\n  std::cout << a1.val + a2.val << std::endl;\r\n\r\n  return 0;\r\n}','Copying class objects',4,69,6294,3229,89,'Ivan Novick',3);
INSERT INTO `question` VALUES (89,'Which lines below are ill-formed?','c','14','16','18','14, 16','16, 18','It is ill-formed to use an implicitly defined assignment operator when one of the members that will need to be copied is a reference. (Line 18)','1 #include &lt;iostream&gt;\r\n2\r\n3 struct A\r\n4 {\r\n5   A(int& var) : r(var) {}\r\n6\r\n7   int &r;\r\n8 };\r\n9\r\n10 int main(int argc, char** argv)\r\n11 {\r\n12   int x = 23;\r\n13\r\n14   A a1(x);\r\n15\r\n16   A a2 = a1;\r\n17\r\n18   a2 = a1;\r\n19\r\n20   return 0;\r\n21 }','Copying class objects',4,70,7483,3541,90,'Ivan Novick',3);
INSERT INTO `question` VALUES (90,'What gets printed?','c','20','30','38','40','The code is ill-formed','When calling global foo, the function is overloaded and each foo is called once depending on the type of the argument.  When calling the member foo, Gateway::foo hides Computer::foo so Gateway::foo is called twice.','#include &lt;iostream&gt;\r\n\r\nint foo(int i)\r\n{\r\n  return 2;\r\n}\r\n\r\ndouble foo(double d)\r\n{\r\n  return 4.0;\r\n}\r\n\r\nstruct Computer\r\n{\r\n  int foo(int i)\r\n  {\r\n    return 8; \r\n  }\r\n};\r\n\r\nstruct Gateway : public Computer\r\n{\r\n  double foo(double d)\r\n  {\r\n    return 16.0; \r\n  }\r\n};\r\n\r\nint main(int argc, char** argv)\r\n{\r\n  Gateway g;\r\n\r\n  std::cout << foo(1) + foo(1.0) + g.foo(1) + g.foo(1.0) << std::endl;\r\n\r\n  return 0;\r\n}','Function Overloading',4,71,8273,4094,91,'Ivan Novick',3);
INSERT INTO `question` VALUES (91,'What gets printed?','e','2','6','7','8','code is ill-formed','A member function template can not be virtual.','#include &lt;iostream&gt;\r\n\r\nstruct mybase\r\n{\r\n  int x;\r\n  \r\n  template &lt;int RANGE&gt;\r\n  virtual void print()\r\n  {\r\n    std::cout << RANGE + x + 1 << std::endl;\r\n  }\r\n};\r\n\r\nstruct myderived : public mybase\r\n{\r\n  template &lt;int RANGE&gt; \r\n  void print()\r\n  {\r\n    std::cout << RANGE + x + 2 << std::endl;\r\n  }\r\n};\r\n\r\nint main(int argc, char** argv)\r\n{\r\n  mybase* b = new myderived;\r\n\r\n  b->x = 1;\r\n\r\n  b->print&lt;5&gt;();\r\n\r\n  return 0;\r\n}','Member Templates',4,72,6060,3530,173,'Ivan Novick',3);
INSERT INTO `question` VALUES (92,'What will be the value of $keys after execution of the following code?','c','undef','0','1','2','code will fail','both $var and $var->{key1} will spring into existence, so keys(%{$var}) == 1','my $var;\r\n\r\nif (exists $var->{key1}->{key2}) {\r\n    $var->{key1}->{key2} = 1;\r\n}\r\n\r\nmy $keys = keys(%{$var});','autovivification',2,15,5961,3238,93,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (93,'What gets printed?','c','undef','0','3',NULL,NULL,'Perl evaluates the right side of the assignment before \'my $a\' in the sub foo()  masks the global $a','my $a = 3;\r\n\r\nprint foo(), \"\\n\";\r\n\r\nsub foo {\r\n    my $a = $a;\r\n}','masking',2,16,6206,4057,94,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (94,'What gets printed?','c','0','1','2','3','0 1 2','There is no list here, but the comma operator in scalar context. This causes the last value to be returned.','my $a = (0, 1, 2);\r\n\r\nprint \"$a\\n\";','context',2,17,8624,3957,95,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (95,'What will be printed?','b','true','false',NULL,NULL,NULL,'This is a special case of automatic conversion between strings and numbers','my $val = \'0\';\r\n\r\nif ($val) {\r\n    print \"true\\n\";\r\n} else {\r\n    print \"false\\n\";\r\n}\r\n','literals',2,18,5281,3505,96,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (96,'What gets printed?','c','ab(newline)','a\\b(newline)','a\\b\\n','a\\\\b(newline)','a\\\\b\\n','\\\\ is a special case in single-quoted string literals, which gives \\, \\n is not interpolated in single-quoted string literals','my $str = \'a\\\\b\\n\';\r\n\r\nprint $str;\r\n','literals',2,19,8835,3846,97,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (97,'What will be the value of $str after execution of the following code?','c','112133','1233','1213','12133',NULL,'this finds pairs of characters and replaces them with a single instance','my $str = \'112133\';\r\n\r\n$str =~ s/(.)\\1/$1/g;','regex',2,20,6432,3683,98,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (98,'What will be printed by the following code?','a','A','B','the code will fail',NULL,NULL,'bless() with omitted class name uses the name of the current package, so new() in this code always creates instances of A','package A;\r\n\r\nsub new { return bless {}; }\r\nsub foo { return \'A\'; }\r\n\r\n\r\npackage B;\r\nuse base \'A\';\r\n\r\nsub foo { return \'B\'; }\r\n\r\n\r\npackage main;\r\n\r\nmy $obj = B->new();\r\n\r\nprint $obj->foo(), \"\\n\";\r\n','object-oriented Perl',2,21,4325,2366,99,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (99,'What will be printed by the following code?','b','A','B1','B2','the code is ill-formed',NULL,'Perl is looking for methods in base classes in the order of mentioning them in the \'use base\' statement','package A;\r\n\r\nsub new { return bless {}, shift };\r\nsub foo { return \'A\' }\r\n\r\n\r\npackage B1;\r\nuse base \'A\';\r\n\r\nsub foo { return \'B1\' }\r\n\r\n\r\npackage B2;\r\nuse base \'A\';\r\n\r\nsub foo { return \'B2\' }\r\n\r\n\r\npackage C;\r\nuse base \'B1\', \'B2\';\r\n\r\n\r\npackage main;\r\n\r\nmy $obj = C->new();\r\n\r\nprint $obj->foo(), \"\\n\";\r\n','object-oriented Perl',2,22,4575,2271,100,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (100,'What gets printed?','c','empty string','value','the code will fail',NULL,NULL,'$self->init() is called before $self is blessed, so the code will fail with the following message: Can\'t call method \"init\" on unblessed reference','package A;\r\n\r\nsub new {\r\n    my $class = shift;\r\n    my $self = {};\r\n    $self->init();\r\n    return bless $self, $class;\r\n};\r\n  \r\nsub init {\r\n    my $self = shift;\r\n    $self->{key} = \'value\';\r\n}\r\n \r\nsub get {\r\n    my ($self, $key) = @_;\r\n    return $self->{$key}; \r\n}\r\n \r\n\r\npackage main;\r\n\r\nmy $obj = A->new();\r\nprint $obj->get(\'key\'), \"\\n\";','object-oriented Perl',2,23,3424,1607,101,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (101,'What gets printed?','b','same','different','the code is ill-formed',NULL,NULL,'0123 is an octal literal, but 123 is a decimal one','my $a = 123;\r\nmy $b = 0123;\r\n\r\nif ($a == $b) {\r\n    print \"same\";\r\n} else {\r\n    print \"different\";\r\n}\r\n','literals',2,24,4519,3122,102,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (102,'What gets printed?','a','same','different','the code is ill-formed',NULL,NULL,'using numerical == operator forces conversion from strings to numbers, which is decimal','my $a = \'123\';\r\nmy $b = \'0123\';\r\n\r\nif ($a == $b) {\r\n    print \"same\";\r\n} else {\r\n    print \"different\";\r\n}\r\n','literals, conversion',2,25,4265,2583,103,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (103,'What gets printed?','a','same','different','the code is ill-formed',NULL,NULL,'_ can be used to separate triplets in numeric literals for readability','my $a = 123456;\r\nmy $b = 123_456;\r\n\r\nif ($a == $b) {\r\n    print \"same\";\r\n} else {\r\n    print \"different\";\r\n}','literals',2,26,4797,2811,104,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (104,'What will be the value of $size after executing the following code?','c','undef','0','1','2','3','$#a = 0 truncates the array to one element (0)','my @a = (0, 1, 2);\r\n\r\n$#a = 0;\r\n\r\nmy $size = @a;','arrays',2,27,6875,3083,105,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (105,'What will be printed?','e','1','2','3','undef','the code is ill-formed','$val is not defined outside of the for {} block, so the code will fail with the following message: \"Global symbol \"$val\" requires explicit package name at ...\"','use strict;\r\nmy @a = (1, 2, 3);\r\n\r\nmy %b = (2 => undef);\r\n\r\nfor my $val (@a) {\r\n    last if $b{$val};\r\n}\r\n\r\nprint $val;','scope',2,28,5175,2258,106,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (106,'What will be the value of $val?','e','undef','aa','cccc','0','3','this will count the number of words','my $str = \'aa bb cccc\';\r\n\r\nmy $val = () = $str =~ /\\w+/g;\r\n','regex, context',2,29,4698,2640,107,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (107,'What will the @b array consist of?','c','(1, 5, 10)','(10, 5, 1)','(1, 10, 5)','(5, 10, 1)','()','sort() compares elements as strings by default','my @a = (10, 5, 1);\r\n\r\nmy @b = sort @a;','sort',2,30,6081,3097,108,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (108,'What will be the value of $keys?','d','undef','0','1','2','the code is ill-formed','any scalar key can be used in hashes, keys() in scalar context returns the number of keys','my %hash;\r\n\r\n$hash{undef} = undef;\r\n$hash{\'\'} = \'\';\r\n\r\nmy $keys = keys(%hash);','hashes',2,31,4843,2204,109,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (109,'What will be the value of $count after executing the following code?','e','0','1','2','3','the code if ill-formed','calling keys() resets each(), so this code will never leave the loop','my %hash = (a => 1, b => 2);\r\n\r\nmy $count = 0;\r\n\r\nwhile (my ($key, $value) = each(%hash)) {\r\n    $count++ if $value == keys(%hash)\r\n}\r\n','hashes',2,32,3899,1679,110,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (110,'What will be printed?','c','0','1','2','3','the code will fail','the final comma is ignored','my @a = (0, 1,);\r\n\r\nprint scalar(@a), \"\\n\";\r\n','lists',2,33,3352,2057,111,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (111,'What will be printed?','b','0','1','2','3','the code will fail','nextval() accesses the $id, which is local to the BEGIN {} block','BEGIN {\r\n    my $id = 0;\r\n    sub nextval {\r\n        $id++;\r\n        return $id;\r\n    }\r\n}\r\n\r\nmy $id = 2;\r\n$id = nextval();\r\nprint $id;','closure',2,34,3368,1684,112,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (112,'What will be printed?','c','2','6','106','I don\'t like undefs','','The message from die() in this code is ignored, 100 is added when exception is caught','my @a = (1, undef, 2);\r\n\r\nmy $sum = 0;\r\nforeach my $val (@a) {\r\n    eval {\r\n        $sum += foo($val);\r\n    };\r\n    if ($@) {\r\n        $sum += 100;\r\n    }\r\n}\r\n\r\nprint \"$sum\\n\";\r\n\r\nsub foo {\r\n    my $val = shift;\r\n    die \"I don\'t like undefs\" unless defined $val;\r\n    return $val + $val;\r\n}','exceptions',2,35,2106,1364,113,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (113,'What will be printed?','d','abc','bac','ab','ba','bca','BEGIN block gets executed before everything else, function foo() is never called','print \'a\';\r\n\r\nBEGIN {\r\n    print \'b\';\r\n}\r\n\r\nsub foo {\r\n    print \'c\';\r\n}','begin block',2,36,2903,1618,114,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (114,'Which messages will be printed in the code below?','a','both','hello world!','goodbye world!','neither','the code will fail','DESTROY() gets called because $obj reference, blessed to the class A, reaches the end of life','package A;\r\n\r\nsub NEW {\r\n    print \"hello world!\\n\";\r\n    return bless {};\r\n};\r\n\r\nsub DESTROY {\r\n    print \"goodbye world!\\n\";\r\n};\r\n\r\n\r\npackage main;\r\n\r\nmy $obj = A::NEW();','object-oriented Perl',2,37,2914,1314,115,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (115,'What gets printed?','b','1','2','3','the code will fail',NULL,'the END block gets executed after everything else','my $a = 1;\r\n\r\n{\r\n    $a = 2;  \r\n}\r\n\r\nEND {\r\n    $a = 3;\r\n}\r\n\r\nprint $a;\r\n','end block',2,38,3153,1576,116,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (116,'What will be printed?','d','abcd','acbd','cadb','acdb','the code will fail','Multiple BEGIN blocks are allowed and will execute in the order of definition. Multiple END blocks are also allowed and will execute in the reverse order of definition','BEGIN {\r\n    print \'a\';\r\n}\r\n\r\nEND {\r\n    print \'b\';\r\n}\r\n\r\nBEGIN {\r\n    print \'c\';\r\n}\r\n\r\nEND {\r\n    print \'d\';\r\n}\r\n','begin and end block',2,39,2958,1520,117,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (117,'What will be printed (disregard newlines)?','d','a b c','b','b c','b a c','b c a','END  blocks are executed as late as possible, that is, after perl has finished running the program and just before the interpreter is being exited, even if it is exiting as a result of a die() call. Objects are destructed before END blocks are executed','package A;\r\n\r\nsub new { return bless {} }\r\nsub DESTROY { print \"a\\n\" }\r\n\r\n\r\npackage main;\r\n\r\nmy $a = A->new();\r\n\r\ndie \"b\\n\";\r\n\r\nEND {\r\n    print \"c\\n\";\r\n}\r\n','destructors',2,40,2593,1287,118,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (118,'What gets printed?','d','1 2 3','2 3 4','5 6 7','6 7 8','the code will fail','the second element (index 1) in the array @a is a reference to an anonymous array, which is dereferenced by @{}, and then the slice [1..3] is taken','my @a = ([1, 2, 3, 4], [5, 6, 7, 8]);\r\n\r\nprint join(\' \', @{$a[1]}[1..3]);','arrays',2,41,3264,1605,119,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (119,'What will be printed?','c','1','true','CODE','SCALAR','SUB','ref(EXPR) returns \'CODE\' if EXPR is a reference to a code','my $var = sub { print \'Hello!\' };\r\n \r\nprint ref($var);\r\n','references',2,42,2566,1378,120,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (120,'What gets printed after execution of the following program?','d','A','B','AB','BA','none of the above','If the reference in DESTROY() is re-blessd before the destructor returns, perl will call DESTROY() for the re-blessed object after the current one returns','package A;\r\n\r\nsub new { return bless {}, shift; }\r\nsub DESTROY { print ref(shift); }\r\n\r\n\r\npackage B;\r\nuse base \'A\';\r\n\r\nsub DESTROY {\r\n    my $self = shift;\r\n    print ref($self);\r\n    bless $self, \'A\';\r\n}\r\n\r\n\r\npackage main;\r\n\r\nmy $obj = B->new();\r\n','object-oriented Perl',2,43,2166,1094,121,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (121,'What will be printed?','a','true','false',NULL,NULL,NULL,'isa(CLASS) returns true if its object is blessed into a subclass of CLASS','package A;\r\nsub new { return bless {}, shift; }\r\n\r\npackage B;\r\nuse base \'A\';\r\n\r\npackage main;\r\n\r\nmy $obj = B->new();\r\n\r\nif ($obj->isa(\'A\')) {\r\n  print \'true\';\r\n} else {\r\n  print \'false\';         \r\n}\r\n','object-oriented Perl',2,44,1262,974,122,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (122,'What gets printed?','b','A','AA','Can\'t locate object method \"foo\" via package \"A\"','none of the above',NULL,'The AUTOLOAD method will be called twice: as foo() and as DESTROY()','package A;\r\nsub NEW { bless {}, shift }\r\nsub AUTOLOAD { print ref(shift) }\r\n\r\npackage main;\r\n\r\nmy $obj = NEW A;\r\n\r\n$obj->foo();','autoload',2,45,1805,1027,123,'Alexander Saydakov',3);
INSERT INTO `question` VALUES (123,'Which messages will be printed?','b','both \'can run\' and \'can bark\'','can run','can bark','none of the above','the code will fail','can(NAME) checks if package has a method called NAME, and AUTOLOAD() does not mean that any method is there','package Animal;\r\nsub AUTOLOAD {}                                   \r\nsub new { bless {}, shift }\r\n\r\npackage Dog;\r\nuse base \'Animal\';\r\nsub run {}\r\n\r\npackage main;\r\n\r\nmy $obj = Dog->new();\r\n\r\nif ($obj->can(\'run\')) {\r\n    print \"can run\\n\"  \r\n}\r\n \r\nif ($obj->can(\'bark\')) {\r\n    print \"can bark\\n\"  \r\n}\r\n','can, autoload',2,46,1969,941,133,'Alexander Saydakov',3);
