INSERT INTO `question` VALUES (299,'What is wrong with this code?','c','exception is not declared','more specific exception type must be used in catch','potential resource leak','return must be outside of the try block',NULL,'If an exception is thrown in the try block, an open file can be left behind, which is a resource leak','public String readLine() {\r\n    try {\r\n      BufferedReader br = \r\n         new BufferedReader(new FileReader(\"file.txt\"));\r\n      String line = br.readLine();\r\n      br.close();\r\n      return line;\r\n    } catch (Exception e) {\r\n      throw new RuntimeException(e);\r\n    }\r\n}\r\n','exception handling',5,3,NULL,NULL,300,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (300,'What will be printed?','b','255','-1','NaN','-255','-127','A narrowing conversion of a signed integer to an integral type T simply discards all but the n lowest order bits, where n is the number of bits used to represent type T. This conversion discards all but the lowest 8 bits, all of which are set to 1 (255=0b11111111), which is a two\'s complement representation of -1.','int i = 255;\r\nbyte b = (byte) i;\r\nSystem.out.println(b);','type conversion',5,4,NULL,NULL,301,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (301,'What will be printed?','a','-0.0','0','0.0','-0','NaN','Floating point numbers are stored using the IEEE 754 standard, which has distinct values for 0.0 and -0.0.','double value = -0.0;\r\nSystem.out.println(value);','types',5,5,NULL,NULL,302,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (302,'What will be printed?','a','true','false',NULL,NULL,NULL,'According to the IEEE 754 standard, Ã¢Ë†â€™0.0 and +0.0 are distinct values, though they both compare as equal.','boolean value = -0.0 == 0.0;\r\nSystem.out.println(value);','types',5,6,NULL,NULL,303,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (303,'What will be printed?','b','true','false',NULL,NULL,NULL,'According to the Java 8 specification, the equality operator == returns false if either operand is NaN','boolean value = Double.NaN == Double.NaN;\r\nSystem.out.println(value);\r\n','types and operations',5,7,NULL,NULL,304,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (304,'What will be printed?','a','true','false',NULL,NULL,NULL,'According to Java 8 specifications, two distinct floating-point values, except +0.0 and -0.0, are considered unequal by the equality operators. In particular, there is one value representing positive infinity and one value representing negative infinity, each compares equal only to itself, and each compares unequal to all other values.','boolean value = \r\n  Double.POSITIVE_INFINITY == Double.POSITIVE_INFINITY;\r\n\r\nSystem.out.println(value);','types and operations',5,8,NULL,NULL,305,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (305,'What will be printed','c','0','1','-1','2147483647','-2147483647','The value of n >> s is n right-shifted s bit positions with sign extension. \r\n\r\nTwo\'s complement representation of -1 has all bits set to 1, so shifting it to the right with sign extension has no effect.\r\n  \r\nHere is the value of a before the operation: 0xFFFFFFFF.  \r\n\r\nAfter shifting it to the right WITH sign extension it is still: 0xFFFFFFFF','int a = -1;\r\na = a >> 1;\r\nSystem.out.println(a);','types and operations',5,9,NULL,NULL,306,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (306,'What will be printed?','d','-1','1','0','2147483647','-2147483647','The value of n >>> s is n right-shifted s bit positions with zero extension. \r\n\r\nTwo\'s complement representation of -1 has all bits set to 1, so shifting it to the right once with zero extension sets the sign bit to 0, which is the max positive value of 2147483647.','int a = -1;\r\na = a >>> 1;\r\nSystem.out.println(a);\r\n','types and operations',5,10,NULL,NULL,307,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (307,'What will be printed?','c','64','128','-64','-128',NULL,'step 1:\r\nNarrowing conversion of int literal 128 to byte results in -128 (0x80)\r\nstep 2:\r\nb >>>= 1 is equivalent to b = b >>> 1\r\nstep 3:\r\nThe operation is performed on int type, so -128 undergoes a widening conversion, which results in -128 of type int (0xffffff80)\r\nstep 4:\r\nThe right-shifting with zero extension results in 0x7fffffc0\r\nstep 5:\r\nThe assignment of the result to b requires a narrowing conversion, which discards all but the lowest 8 bits (0xc0), which is a two\'s complement representation of -64','byte b = (byte) 128;\r\nb >>>= 1;\r\nSystem.out.println(b);','types and operations',5,11,NULL,NULL,308,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (308,'What will be the result of compiling and executing this code?','c','\"the string is null\"','\"null\"','NullPointerException','compilation error',NULL,'s is null, so calling isEmpty() method on a null object reference will throw the NullPointerException.','String s = null;\r\nif ((s != null) || (!s.isEmpty())) {\r\n  System.out.println(s);\r\n} else {\r\n  System.out.println(\"the string is null\");\r\n}\r\n','expressions and operations',5,12,NULL,NULL,309,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (309,'What will be printed?','b','true','false',NULL,NULL,NULL,'This equality test determines whether or not the two operands refer to the same String object. \r\n\r\nThe result is false if the operands are distinct String objects, even if they contain the same sequence of characters.','String s1 = new String(\"a\");\r\nString s2 = new String(\"a\");\r\n\r\nSystem.out.println(s1 == s2);\r\n','operations',5,13,NULL,NULL,310,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (310,'What will be the result of running this code?','a','output: false false','output: false true false','output: true false true','compilation error','run time error','Conditional operator && doesn\'t evaluate the right side if the left side is false.','public class Test {\r\n\r\n  static boolean printAndReturn(boolean value) {\r\n    System.out.println(value);\r\n    return value;\r\n  }\r\n\r\n  public static void main(String[] args) {\r\n    if (printAndReturn(false) && printAndReturn(true)) {\r\n      System.out.println(true);\r\n    } else {\r\n      System.out.println(false);\r\n    }\r\n  }\r\n\r\n}','expressions and operations',5,14,NULL,NULL,311,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (311,'What will be printed?','d','1','2','4','5','6','This is equivalent of a = a + (++a * a). The left-hand operand is saved before the right-hand operand is evaluated. So this results in 1 + 2 * 2 = 5.','int a = 1;\r\na += ++a * a;\r\nSystem.out.println(a);','types and operations',5,15,NULL,NULL,312,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (312,'What will be printed?','a','true','false',NULL,NULL,NULL,'Compile-time constant expressions of type String are always \"interned\" so as to share unique instances, using the method String.intern','String s1 = \"a\";\r\nString s2 = \"a\";\r\nSystem.out.println(s1 == s2);','types and operations',5,16,NULL,NULL,313,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (313,'What will be printed?','a','a','b','ab',NULL,NULL,'Strings are immutable, so s1 += \"b\" produces a new instance of String, while s2 still referring to the instance containing \"a\".','String s1 = \"a\";\r\nString s2 = s1;\r\ns1 += \"b\";\r\nSystem.out.println(s2);','types and operations',5,17,NULL,NULL,314,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (314,'What will be printed?','c','0','1','-1','10','20','Flipping all bits (one\'s complement) is offset by -1 from the negative value (two\'s complement). \r\n\r\nIn other words, -x equals (~x)+1. Therefore the result is 10 + (-10 - 1) = -1','int a = 10;\r\nSystem.out.println(a + ~a);','types and operations',5,18,NULL,NULL,315,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (315,'Which of the following is true?','a','The code won\'t compile','There will be a run time error','The code will print \"a\"','The code will print \"b\"',NULL,'The code won\'t compile with the error: A is not public in a; cannot be accessed from outside package','package a;\r\nclass A {\r\n  public void foo() {\r\n     System.out.println(\"a\");\r\n  }\r\n}\r\n\r\npackage b;\r\nimport a.A;\r\npublic class B extends A {\r\n  public void foo() {\r\n    System.out.println(\"b\");\r\n  }\r\n\r\n  public static void main(String[] args) {\r\n    new B().foo();\r\n  }\r\n}','classes and packages',5,19,NULL,NULL,316,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (316,'What of following will happen?','a','The code will not compile','There will be a run time error','The code will print 0','The code will print 10',NULL,'The code won\'t compile with error: generic array creation','public class A&lt;T&gt; {\r\n\r\n  public void main(String[] args) {\r\n    A&lt;String&gt;[] array = new A&lt;String&gt;[10];\r\n    System.out.println(array.length);\r\n  }\r\n\r\n}\r\n','classes and generics',5,20,NULL,NULL,317,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (317,'Which of the following is true?','b','class C cannot be instantiated because of private constructor','class C cannot be instantiated because it is abstract','method getInstance() cannot be static','there is nothing wrong with this code',NULL,'error: C is abstract; cannot be instantiated','public interface I {\r\n  public void method();\r\n}\r\n\r\npublic abstract class C implements I {\r\n  private C() {\r\n  }\r\n\r\n  public static C getInstance() {\r\n    return new C();\r\n  }\r\n\r\n  public void method() {\r\n  }\r\n}\r\n','classes and interfaces',5,21,NULL,NULL,318,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (318,'Which of the following is true?','d','The code won\'t compile','The code will fail at run time','The code will print \"Base\"','The code will print \"Derived\"',NULL,'There is nothing wrong. The overridden method will be called printing \"Derived\".','public class Base {\r\n  public String toString() {\r\n    return \"Base\";\r\n  }\r\n}\r\n\r\npublic class Derived extends Base {\r\n  public String toString() {\r\n    return \"Derived\";\r\n  }\r\n}\r\n\r\npublic class Main {\r\n  public static void main(String[] args) {\r\n    Base b = new Derived();\r\n    System.out.println(b);\r\n  }\r\n}','classes',5,22,NULL,NULL,319,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (319,'Which of the following is true?','b','The code will print \"a\"','The code will print \"b\"','The code won','The code will fail at run time',NULL,'Nothing is wrong here. The cast to A is unnecessary. \r\n\r\nThe overridden method will be called producing \"b\".','public class A {\r\n  public String foo() {\r\n    return \"a\";\r\n  }\r\n}\r\n\r\npublic class B extends A {\r\n  public String foo() {\r\n    return \"b\";\r\n  }\r\n}\r\n\r\npublic class Main {\r\n  public static void main(String[] args) {\r\n    A a = (A) new B();\r\n    System.out.println(a.foo());\r\n  }\r\n}\r\n','classes',5,23,NULL,NULL,320,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (320,'Which of the following is true?','c','The code will print \"hello\"','The code won\'t compile','The code will fail at run time',NULL,NULL,'The code will fail at run time with error:\r\nMain method is not static in class Main, please define the main method as:\r\npublic static void main(String[] args)\r\n','public final class Main {\r\n\r\n  static final String s = \"hello\";\r\n\r\n  public void main(String[] args) {\r\n    System.out.println(s);\r\n  }\r\n\r\n}','classes and methods',5,24,NULL,NULL,321,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (321,'Which of the following is true?','a','The code will print \"4\"','The code will print \"1234\"','The code won\'t compile','The code will fail at run time',NULL,'\"1234\" is equivalent of new String(\"1234\").intern()','public class Main {\r\n  public static void main(String[] args) {\r\n    System.out.println(\"1234\".length());\r\n  }\r\n}','strings',5,25,NULL,NULL,322,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (322,'Which of the following is true?','c','class cannot be instantiated because of non-public constructor','class cannot be final','class cannot be static','class cannot be named Class because it is a reserved keyword','nothing is wrong with this code','Class.java:5: error: modifier static not allowed here\r\npublic static final class Class {\r\n                    ^\r\n','public static final class Class {\r\n  static final int id = 1;\r\n\r\n  Class() {\r\n  }\r\n\r\n  static public int id() {\r\n    return id;\r\n  }\r\n\r\n}','classes',5,26,NULL,NULL,323,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (323,'Which of the following is true?','d','member variable can not be final','constructor parameter cannot be final','compareTo must take Object as parameter','operator &gt; is not defined for type A','nothing is wrong with this code','The type of each of the operands of a numerical comparison operator must be a type that is convertible to a primitive numeric type, or a compile-time error occurs.','public class A implements Comparable&lt;A&gt; {\r\n\r\n  final int a;\r\n\r\n  A(final int a) {\r\n    this.a = a;\r\n  }\r\n\r\n  public int compareTo(A that) {\r\n    return this.a - that.a;\r\n  }\r\n\r\n  public static void main(String[] args) {\r\n    A a1 = new A(1);\r\n    A a2 = new A(2);\r\n    System.out.println(a1 &gt; a2);\r\n  }\r\n\r\n}\r\n','classes',5,27,NULL,NULL,324,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (324,'which of the following will happen?','e','compile time error: X is not Comparable','run time error: X is not Comparable','compile time error: accessing private member X.x in compare()','output: [1, 2, 3]','output: [3, 2, 1]','Nothing is wrong with the code. The array will be sorted according to the supplied anonymous Comparator in descending order.','import java.util.Arrays;\r\nimport java.util.Comparator;\r\n\r\npublic class X {\r\n\r\n  private int x;\r\n\r\n  public X(int x) {\r\n    this.x = x;\r\n  }\r\n\r\n  public String toString() {\r\n    return Integer.toString(x);\r\n  }\r\n\r\n  public static void main(String[] args) {\r\n    X[] array = new X[3];\r\n    array[0] = new X(3);\r\n    array[1] = new X(1);\r\n    array[2] = new X(2);\r\n\r\n    Arrays.sort(array, new Comparator<X>() {\r\n      public int compare(X x1, X x2) {\r\n        return x2.x - x1.x;\r\n      }\r\n    });\r\n\r\n    System.out.println(Arrays.toString(array));\r\n  }\r\n}\r\n','syntax',5,28,NULL,NULL,343,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (325,'What are possible ways to create a new database in a PostgreSQL system?','e','From psql execute \'NEW DATABASE\'','From psql execute \'CREATE DATABASE\'','Run the command line tool \'createdb\'','Either the first or second option  above','Either the second or third option above','PostgreSQL databases can be created inside the PSQL shell via SQL commands or from the command line tools.  There are a number of command line tools including \'createdb\' which come with PostgreSQL.  From the SQL Commands, \'CREATE DATABASE\' is the syntax.',NULL,'new database',7,1,0,0,326,NULL,1);
INSERT INTO `question` VALUES (326,'True or False?  There can be multiple databases in one schema.','b','True','False',NULL,NULL,NULL,'This is false.  Databases are a higher level construct than Schemas.  There can be multiple schemas defined within a given database.',NULL,'schema',7,2,0,0,327,NULL,1);
INSERT INTO `question` VALUES (327,'What is output by the SQL below?','d','1','2','4','8','16','The code will produce a total of four rows in table foo with the same values in each row 1,2,3.  Column b have the value 2 in each row, and so the query returns the sum of four elements with value 2 which equals 8.','DROP TABLE IF EXISTS foo;\r\nCREATE TABLE foo (a INT, b INT, c INT);\r\nINSERT INTO foo VALUES (1,2,3);\r\nINSERT INTO foo SELECT * FROM foo;\r\nINSERT INTO foo SELECT * FROM foo;\r\nSELECT sum(b) FROM foo;','basic sql',7,3,0,0,328,NULL,1);
INSERT INTO `question` VALUES (328,'What is output by the SQL below?','b','0','2','8','16','NULL','foo.foo and koo.foo are distinctly different tables in different schemas.  Therefore tracing the logic there is only one row in the table foo.foo and the resulting output is the value 2.','DROP SCHEMA IF EXISTS foo CASCADE;\r\nDROP SCHEMA IF EXISTS koo CASCADE;\r\nCREATE SCHEMA foo;\r\nCREATE SCHEMA koo;\r\nCREATE TABLE koo.foo (a INT, b INT, c INT);\r\nCREATE TABLE foo.foo (a INT, b INT, c INT);\r\nINSERT INTO foo.foo VALUES (1,2,3);\r\nINSERT INTO koo.foo SELECT * FROM koo.foo;\r\nINSERT INTO koo.foo SELECT * FROM koo.foo;\r\nSELECT sum(b) FROM foo.foo;','schemas',7,4,0,0,329,NULL,1);
INSERT INTO `question` VALUES (329,'How is the schema search path found in postgresql?','a','show search_path;','show search path;','select * from search_path','select * from paths where name = \'search\'','select search_path from gucs;','search_path is a GUC, and GUCs can be found by using the show command.',NULL,'schema search path',7,5,0,0,331,NULL,1);
INSERT INTO `question` VALUES (331,'Which are valid ways to get a list of databases in an instance?','d','select datname from pg_database;','psql -l','show databases;','The first two options above','All three options above','psql -l is a quick way to get the database list from the command line.\n\nThe \'pg_database\' table is a shared catalog table that contains metadata about all databases.\n\n\'show databases\' is not a valid command in postgresql.',NULL,'list databases',7,6,0,0,332,NULL,1);
INSERT INTO `question` VALUES (332,'Which are valid ways to get a list of schemas in an database?','d','select nspname from pg_namespace;','\\dn','psql -s','The first two options above','All three options above','The \'pg_namespace\' table is a per database catalog table that contains metadata about all schemas in the database.\n\n\'\\dn\' will list all schemas from inside psql shell.\n\n\'psql -s\' is not a valid command to list schemas.',NULL,'list schemas',7,7,0,0,333,NULL,1);
INSERT INTO `question` VALUES (333,'Which are valid ways to get a list of tables in an database?','e','select tablename from pg_tables;','\\dt *.*','select relname, relkind from pg_class where relkind = \'r\';','The first two options above','All three options above','\'pg_tables\' is a catalog view that shows all tables with metadata in a database.\n\n\'\\dt *.*\' will list all tables in all schemas in the current database from the psql shell.\n\n\'pg_class\' is a catalog table that contains infomation about all relations in a postgresql database including tables.  In order to limit to tables only filter on the relkind column.',NULL,'list tables',7,8,0,0,334,NULL,1);
INSERT INTO `question` VALUES (334,'True or False?  After a PostgreSQL database cluster is created the charachter encoding is fixed for the life of the database cluster.','b','true','false',NULL,NULL,NULL,'New databases can be created with a different encoding than the default encoding by specifying the encoding parameter when creating a new database.','initdb --encoding=UTF8','encoding',7,9,0,0,335,NULL,1);
INSERT INTO `question` VALUES (335,'True or False?  After a database is created the charachter encoding is fixed for the life of the database.','a','true','false',NULL,NULL,NULL,'Once a database is created, its encoding can never be changed. Alter database can not change the encoding.','CREATE DATABASE foo ENCODING \'UTF8\'','encoding',7,10,0,0,336,NULL,1);
INSERT INTO `question` VALUES (336,'Which are valid ways to find the encoding parameters of existing databases.','d','select * from pg_database;','psql -l','show pg_encoding;','The first two options above','All three options above','psql -l and select * from pg_database will both show the encoding and locale information which is set at the database level and not changed after database creation.\n\n$ psql -l\n List of databases\n Name           |  Owner   | Encoding | Collate | Ctype |   Access privileges\n --------------------------+----------+----------+---------+-------+-----------------------\n demo                     | postgres | UTF8     | C       | C     |\n postgres                 | postgres | UTF8     | C       | C     |\n template0                | postgres | UTF8     | C       | C     | =c/postgres          +\n |          |          |         |       | postgres=CTc/postgres\n template1                | postgres | UTF8     | C       | C     | =c/postgres          +\n |          |          |         |       | postgres=CTc/postgres',NULL,'encoding',7,11,0,0,337,NULL,1);
INSERT INTO `question` VALUES (337,'Which database(s) are created immediately by the initdb command when a PostgreSQL database cluster is created?','e','template0','template1','postgres','The first two above','All three above','All three are created automatically.\ntemplate0 is never modified and can be used as a template when creating new databases.\ntemplate1 can be modified by the DBA and is used by default as a template when creating new databases.\npostgres is a default database meant for use by users, utilities and third party applications.',NULL,'template dbs',7,12,0,0,338,NULL,1);
INSERT INTO `question` VALUES (338,'True or False?  Checksums are enabled on data pages by default in PostgreSQL','b','true','false',NULL,NULL,NULL,'false.  By default data pages do not have checksums.\nIn order to prevent silent data corruption on disk, checksums should be enabled during initdb with --data-checksums.\nThis setting can not be changed after initdb.',NULL,'data checksums',7,13,0,0,339,NULL,1);
INSERT INTO `question` VALUES (339,'True or False?  The code below will prevent access to the \'demo\' database for all users including super users.','a','true','false',NULL,NULL,NULL,'True\nEven superusers can not connect to \'demo\' after this command.','alter database demo with ALLOW_CONNECTIONS false;','database connections',7,14,0,0,340,NULL,1);
INSERT INTO `question` VALUES (340,'At what level can configuration parameters be set?','e','system','database','role','The first two above','All three above','These are all valid syntax below:\nalter system set log_duration=off;\nalter database demo set log_duration=on;\nalter role george set log_duration=on;',NULL,'configuration parameters',7,15,0,0,341,NULL,1);
INSERT INTO `question` VALUES (341,'What is the command equivalent to psql -l from inside the psql prompt?','e','list','show','show all','show list','\\l','The psql program\'s \\l meta-command is equivalent','$ psql -l\n List of databases\n Name           |  Owner   | Encoding | Collate | Ctype |   Access privileges\n --------------------------+----------+----------+---------+-------+-----------------------\n demo                     | postgres | UTF8     | C       | C     |\n postgres                 | postgres | UTF8     | C       | C     |\n template0                | postgres | UTF8     | C       | C     | =c/postgres          +\n |          |          |         |       | postgres=CTc/postgres\n template1                | postgres | UTF8     | C       | C     | =c/postgres          +\n |          |          |         |       | postgres=CTc/postgres\n (5 rows)\n \n ','database list',7,16,0,0,342,NULL,1);
INSERT INTO `question` VALUES (342,'Assuming the code below was already run by a superuser, what happens when user george tries to create a table in the rawdata schema?','a','table successfully created','george does not have access to the schema','the schema is not found','the table will be accidentaly created in the public schema','george cannot login','\'george\' is a member of the \'engineers\' group.  The \'engineers\' have access to the schema named \'rawdata\' schema, so the operation is successful and the new table is created in the \'rawdata\' schema.','CREATE ROLE engineers;\nCREATE ROLE george WITH LOGIN;\nGRANT engineers TO george;\nGRANT ALL ON SCHEMA rawdata TO engineers;\n','grant role',7,17,0,0,347,NULL,1);
INSERT INTO `question` VALUES (343,'What will be the result of compiling and executing this code?','b','compilation error','NullPointerException','\"the string is null\"','\"null\"',NULL,'Logical operator & always evaluates operands on both sides. The value of s is null, therefore calling isEmpty() method on a null object reference will throw the NullPointerException.','String s = null;\r\nif ((s != null) & (!s.isEmpty())) {\r\n  System.out.println(s);\r\n} else {\r\n  System.out.println(\"the string is null\");\r\n}','operators',5,29,NULL,NULL,344,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (344,'Is the following a valid expression in Java 1.8?','a','yes','no',NULL,NULL,NULL,'This is a valid lambda expression with no parameters and expression body returning double.','java.util.function.Supplier&lt;Double&gt; supplier = () -> 0.0;','lambdas',5,30,NULL,NULL,345,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (345,'Is the following a valid lambda expression in Java 1.8?','b','yes','no',NULL,NULL,NULL,'Only parameters with declared types can have modifiers. Parameter s has inferred type and therefore cannot have the final modifier.','import java.util.function.Consumer;\r\n\r\npublic class X {\r\n  static void process(String str, Consumer&lt;String&gt; consumer) {\r\n    consumer.accept(str);\r\n  }\r\n\r\n  public static void main(String[] args) {\r\n    process(\"a\", (final s) -> System.out.println(s));\r\n  }\r\n}','lambdas',5,31,NULL,NULL,346,'Alexander Saydakov',1);
INSERT INTO `question` VALUES (346,'Is the following a valid lambda expression in Java 1.8?','b','yes','no',NULL,NULL,NULL,'It is not possible for a lambda expression to declare the types of some of its parameters but leave others to be inferred.','import java.util.function.BiConsumer;\r\n\r\npublic class X {\r\n  static void process(String str1, String str2, BiConsumer<String, String> consumer) {\r\n    consumer.accept(str1, str2);\r\n  }\r\n\r\n  public static void main(String[] args) {\r\n    process(\"a\", \"b\", (String s1, s2) -> System.out.println(s1 + s2));\r\n  }\r\n}\r\n','lambdas',5,32,NULL,NULL,351,'Alexander Saydakov',1);
